let BootGameState = function() {
	console.log("%cStarting my awesome game", "color:white; background:red");
},
	pathToImages = 'src/assets/img',
	slides = [
		'oldpixel', // 5000 0
		'description', // 10 000 1
		'mainmenu00', // 11 000 2
		'mainmenu01', // 12 000 3
		'mainmenu02', // 13 000 4
		'mainmenu03', // 14 000 5
		'mainmenu04', // 15 000 6
		'mainmenu05' // 16 000 7
		];
BootGameState.prototype = {
	preload: function(){
		slides.forEach((e) => {
			this.game.load.image(e, pathToImages + '/CUTSCENS/' + e + '.png');
		});
	},
  	create: function() {
		let slideNumber = 0,
			slide = game.add.sprite(0,0, slides[slideNumber]);
			
		//slide = game.add.sprite(0,0);
		//slide.destroy();
		let timerId = setTimeout(function tick() {
			console.log('destroy slide');
			slideNumber += 1;
			slide.destroy();
			console.log('Next clide', slides[slideNumber]);
			slide = game.add.sprite(0,0, slides[slideNumber]);
			if(slideNumber === 1){
				console.log('Slide 2');
				timerId = setTimeout(tick, 5000);
			} else if (slideNumber === 2) {
				timerId = setTimeout(tick, 400);
			} else if (slideNumber === 7){
				console.log('Clear!');
				clearTimeout(timerId);
				game.state.start('MainMenu');
				slide.destroy();
			} else {
				timerId = setTimeout(tick, 400);
			}
		}, 5000);
	}
};



module.exports.BootGameState = BootGameState;