let MainMenuGameState = function() {
    console.log('Game state: main menu!');
},
    pathToImage = 'src/assets/img',
    stateImages = [
        'mainmenu',
        'btnNewGame',
        'btnLoadGame',
        'btnSettings',
        'btnCredits',
        'btnExit'
    ];
MainMenuGameState.prototype = {
    preload: function() {
        console.log('mainmenu preload');
        stateImages.forEach((e) => {
            this.game.load.image(e, pathToImage + '/MAINMENU/' + e + '.png');
        });        
    },
    create: function() {
        let menuBackground = game.add.sprite(0,0, stateImages[0]),
            btnNewGame = game.add.sprite(461,110, stateImages[1]),
            btnLoadGame = game.add.sprite(461, 164, stateImages[2]),
            btnSettings = game.add.sprite(461, 218, stateImages[3]),
            btnCredits = game.add.sprite(461, 272, stateImages[4]),
            btnExit = game.add.sprite(461, 372, stateImages[5]);

            btnExit.inputEnabled = true;

            btnExit.events.onInputDown.add(exitGame, this);
    }
}

function exitGame() {
    let answer = confirm('You are seriosly want to exit MASTER OF PHASER?');
    if(answer) {
        window.close();
    }
}

module.exports.MainMenuGameState = MainMenuGameState;